import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./MapComponent.css";

// Icons definieren
const getBinIcon = (fillLevel) => {
  if (fillLevel <= 33) {
    return new L.Icon({
      iconUrl: "/icons/bin_icon_green.svg",
      iconSize: [30, 30],
      iconAnchor: [15, 30],
    });
  } else if (fillLevel <= 66) {
    return new L.Icon({
      iconUrl: "/icons/bin_icon_yellow.svg",
      iconSize: [30, 30],
      iconAnchor: [15, 30],
    });
  } else {
    return new L.Icon({
      iconUrl: "/icons/bin_icon_red.svg",
      iconSize: [30, 30],
      iconAnchor: [15, 30],
    });
  }
};

// Hauptkomponente
const MapComponent = () => {
  const [bins, setBins] = useState([]);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [userLocation, setUserLocation] = useState(null); // Zustand fÃ¼r den Benutzerstandort
  const mapRef = useRef(null); // Referenz fÃ¼r die Map-Instanz
  const routeLayerRef = useRef(null); // Referenz fÃ¼r die Route

  // Benutzerstandort ermitteln
  useEffect(() => {
    if (navigator.geolocation) {
      console.log("Geolocation wird unterstÃ¼tzt. Frage Standort an...");
      navigator.geolocation.getCurrentPosition(
        (position) => {
          console.log("Standort erfolgreich ermittelt:", position);
          setUserLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        (error) => {
          console.error("Fehler bei der Standortermittlung:", error);
        },
        {
          enableHighAccuracy: true, // HÃ¶here Genauigkeit
          timeout: 10000, // Timeout nach 10 Sekunden
          maximumAge: 0, // Keine Cache-Nutzung
        }
      );
    } else {
      console.error("Geolocation wird nicht unterstÃ¼tzt.");
    }
  }, []);

  // Automatische Datenaktualisierung
  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:8000/bins");
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log("API-Antwort:", data); // Logge die Antwort
      setBins(data.filter((b) => b.latitude && b.longitude));
      setLastUpdated(new Date());
    } catch (error) {
      console.error("Aktualisierungsfehler:", error);
    }
  };

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 300000); // Alle 5 Minuten aktualisieren
    return () => clearInterval(interval);
  }, []);

  // Route mit OpenRouteService berechnen
  const calculateRoute = async (destination) => {
    if (userLocation && destination && mapRef.current) {
      const map = mapRef.current;

      // Alte Route entfernen
      if (routeLayerRef.current) {
        map.removeLayer(routeLayerRef.current);
      }

      // OpenRouteService API-Key
      const apiKey = "5b3ce3597851110001cf62488ef9c2a0937d4cfeb44e454ec9d93fd3"; // Ersetze dies durch deinen API-Key

      // Koordinaten fÃ¼r die Anfrage
      const start = [userLocation.longitude, userLocation.latitude];
      const end = [destination.longitude, destination.latitude];

      // API-Anfrage an OpenRouteService
      try {
        const response = await fetch(
          `https://api.openrouteservice.org/v2/directions/foot-walking?api_key=${apiKey}&start=${start}&end=${end}`
        );
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();

        // Route auf der Karte anzeigen
        const routeCoordinates = data.features[0].geometry.coordinates.map((coord) => [
          coord[1],
          coord[0],
        ]);

        routeLayerRef.current = L.polyline(routeCoordinates, {
          color: "#1E90FF", // Blaue Linie
          weight: 5,
          opacity: 0.7,
        }).addTo(map);

        // Karte auf die Route zentrieren
        map.fitBounds(routeLayerRef.current.getBounds());
      } catch (error) {
        console.error("Fehler bei der Routenberechnung:", error);
      }
    }
  };

  return (
    <div className="App">
      <div className="status-bar">
        <div>
          Letzte Aktualisierung: {lastUpdated?.toLocaleTimeString() || "Wird geladen..."}
        </div>
        <button onClick={fetchData} className="refresh-button">
          ðŸ”„ Neu laden
        </button>
      </div>

      <MapContainer
        center={userLocation ? [userLocation.latitude, userLocation.longitude] : [52.52, 13.405]} // Zentrierung auf Benutzerstandort oder Berlin
        zoom={13}
        style={{ height: "80vh", width: "100%" }}
        whenCreated={(map) => (mapRef.current = map)} // Map-Instanz in der Referenz speichern
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        />

        {userLocation && (
          <Marker
            position={[userLocation.latitude, userLocation.longitude]}
            icon={L.icon({
              iconUrl: "/icons/location.svg", // Icon fÃ¼r den Benutzerstandort
              iconSize: [30, 30],
              iconAnchor: [15, 30],
            })}
          >
            <Popup>Dein Standort</Popup>
          </Marker>
        )}

        {bins.map((bin) => (
          <Marker
            key={bin.id}
            position={[bin.latitude, bin.longitude]}
            icon={getBinIcon(bin.fill_level)}
            eventHandlers={{
              click: () => calculateRoute(bin), // Route zum MÃ¼lleimer berechnen
            }}
          >
            <Popup>
              <div className="popup">
                <h4>{bin.name}</h4>
                <div
                  className="fill-level"
                  style={{
                    width: `${bin.fill_level}%`,
                    backgroundColor:
                      bin.fill_level > 66
                        ? "#f44336"
                        : bin.fill_level > 33
                        ? "#ffc107"
                        : "#4caf50",
                  }}
                />
                <p>{bin.fill_level}% gefÃ¼llt</p>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default MapComponent;
