import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./MapComponent.css";

// OpenRouteService API-Key (ersetzen!)
const ORS_API_KEY = "5b3ce3597851110001cf62488ef9c2a0937d4cfeb44e454ec9d93fd3";

// Icons definieren
const getBinIcon = (fillLevel) => {
  if (fillLevel <= 33) {
    return new L.Icon({
      iconUrl: "/icons/bin_icon_green.svg",
      iconSize: [30, 30],
      iconAnchor: [15, 30],
    });
  } else if (fillLevel <= 66) {
    return new L.Icon({
      iconUrl: "/icons/bin_icon_yellow.svg",
      iconSize: [30, 30],
      iconAnchor: [15, 30],
    });
  } else {
    return new L.Icon({
      iconUrl: "/icons/bin_icon_red.svg",
      iconSize: [30, 30],
      iconAnchor: [15, 30],
    });
  }
};

// Hauptkomponente
const MapComponent = () => {
  const [bins, setBins] = useState([]);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const mapRef = useRef(null);
  const routingLayerRef = useRef(null); // Layer f√ºr Route

  // Benutzerstandort ermitteln
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        (error) => {
          console.error("Fehler bei der Standortermittlung:", error);
        },
        { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
      );
    } else {
      console.error("Geolocation wird nicht unterst√ºtzt.");
    }
  }, []);

  // API-Daten abrufen
  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:8000/bins");
      if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);

      const data = await response.json();
      setBins(data.filter((b) => b.latitude && b.longitude));
      setLastUpdated(new Date());
    } catch (error) {
      console.error("Aktualisierungsfehler:", error);
    }
  };

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 300000);
    return () => clearInterval(interval);
  }, []);

  // Route mit OpenRouteService berechnen
  const calculateRoute = async (destination) => {
    if (!userLocation || !destination) {
      console.error("Fehlende User-Location oder Destination!");
      return;
    }

    console.log("üìç Starte Routenberechnung zu:", destination);

    const map = mapRef.current;
    if (!map) {
      console.error("Karteninstanz nicht vorhanden!");
      return;
    }

    // Vorherige Route entfernen
    if (routingLayerRef.current) {
      console.log("üîÑ Entferne alte Route...");
      map.removeLayer(routingLayerRef.current);
    }

    try {
      const response = await fetch(
        `https://api.openrouteservice.org/v2/directions/foot-walking?api_key=${ORS_API_KEY}&start=${userLocation.longitude},${userLocation.latitude}&end=${destination.longitude},${destination.latitude}`
      );

      console.log("üåç API-Anfrage gesendet:", response.url);

      if (!response.ok) {
        throw new Error(`Fehlerhafte API-Antwort: ${response.status}`);
      }

      const data = await response.json();
      console.log("üì• API-Antwort erhalten:", data);

      if (!data.routes || data.routes.length === 0) {
        throw new Error("‚ùå Keine Route gefunden!");
      }

      // Koordinaten f√ºr die Route extrahieren
      const routeCoords = data.routes[0].geometry.coordinates.map(([lon, lat]) => [lat, lon]);
      console.log("üó∫Ô∏è Route erhalten:", routeCoords);

      // Linie auf der Karte zeichnen
      routingLayerRef.current = L.polyline(routeCoords, {
        color: "blue",
        weight: 5,
        opacity: 0.7,
      }).addTo(map);

      console.log("‚úÖ Route erfolgreich gezeichnet!");
    } catch (error) {
      console.error("‚ö†Ô∏è Fehler bei der Routenberechnung:", error);
    }
  };

  return (
    <div className="App">
      <div className="status-bar">
        <div>
          Letzte Aktualisierung: {lastUpdated?.toLocaleTimeString() || "Wird geladen..."}
        </div>
        <button onClick={fetchData} className="refresh-button">
          üîÑ Neu laden
        </button>
      </div>

      <MapContainer
        center={userLocation ? [userLocation.latitude, userLocation.longitude] : [52.52, 13.405]}
        zoom={13}
        style={{ height: "80vh", width: "100%" }}
        whenCreated={(map) => (mapRef.current = map)}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        />

        {userLocation && (
          <Marker
            position={[userLocation.latitude, userLocation.longitude]}
            icon={L.icon({
              iconUrl: "/icons/location.svg",
              iconSize: [30, 30],
              iconAnchor: [15, 30],
            })}
          >
            <Popup>Dein Standort</Popup>
          </Marker>
        )}

        {bins.map((bin) => (
          <Marker
            key={bin.id}
            position={[bin.latitude, bin.longitude]}
            icon={getBinIcon(bin.fill_level)}
            eventHandlers={{
              click: () => calculateRoute(bin),
            }}
          >
            <Popup>
              <div className="popup">
                <h4>{bin.name}</h4>
                <div
                  className="fill-level"
                  style={{
                    width: `${bin.fill_level}%`,
                    backgroundColor:
                      bin.fill_level > 66
                        ? "#f44336"
                        : bin.fill_level > 33
                        ? "#ffc107"
                        : "#4caf50",
                  }}
                />
                <p>{bin.fill_level}% gef√ºllt</p>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default MapComponent;
