import React, { useEffect, useState, useRef } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "./MapComponent.css";

// OpenRouteService API-Key (ersetzen!)
const ORS_API_KEY = "5b3ce3597851110001cf62488ef9c2a0937d4cfeb44e454ec9d93fd3";

// Icons f√ºr M√ºlleimer je nach F√ºllstand
const getBinIcon = (fillLevel) => {
  const colors = ["green", "yellow", "red"];
  const colorIndex = fillLevel <= 33 ? 0 : fillLevel <= 66 ? 1 : 2;
  return new L.Icon({
    iconUrl: `/icons/bin_icon_${colors[colorIndex]}.svg`,
    iconSize: [30, 30],
    iconAnchor: [15, 30],
  });
};

const MapComponent = () => {
  const [bins, setBins] = useState([]);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const mapRef = useRef(null);
  const routeLayerRef = useRef(null); // Layer f√ºr die Route

  // Benutzerstandort abrufen
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          console.log("üìç Standort ermittelt:", position.coords);
          setUserLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
          });
        },
        (error) => console.error("‚ö†Ô∏è Standortfehler:", error),
        { enableHighAccuracy: true, timeout: 10000, maximumAge: 0 }
      );
    } else {
      console.error("‚ö†Ô∏è Geolocation wird nicht unterst√ºtzt.");
    }
  }, []);

  // API-Daten abrufen (M√ºlleimer-Standorte & F√ºllst√§nde)
  const fetchData = async () => {
    try {
      const response = await fetch("http://localhost:8000/bins");
      if (!response.ok) throw new Error(`HTTP Fehler: ${response.status}`);

      const data = await response.json();
      console.log("üóëÔ∏è M√ºlleimer geladen:", data);
      setBins(data.filter((b) => b.latitude && b.longitude));
      setLastUpdated(new Date());
    } catch (error) {
      console.error("‚ö†Ô∏è Fehler beim Laden der Daten:", error);
    }
  };

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 300000);
    return () => clearInterval(interval);
  }, []);

  // Route mit OpenRouteService berechnen
  const calculateRoute = async (destination) => {
    if (!userLocation) {
      console.warn("‚ö†Ô∏è Benutzerstandort nicht verf√ºgbar!");
      return;
    }
    if (!destination || !destination.latitude || !destination.longitude) {
      console.warn("‚ö†Ô∏è Ung√ºltiger Zielpunkt:", destination);
      return;
    }

    console.log(`üìç Berechne Route von [${userLocation.latitude}, ${userLocation.longitude}] zu [${destination.latitude}, ${destination.longitude}]`);

    const map = mapRef.current;
    if (!map) {
      console.error("‚ö†Ô∏è Karteninstanz nicht gefunden!");
      return;
    }

    // Vorherige Route entfernen
    if (routeLayerRef.current) {
      console.log("üîÑ Entferne alte Route...");
      map.removeLayer(routeLayerRef.current);
    }

    try {
      const url = `https://api.openrouteservice.org/v2/directions/foot-walking?api_key=${ORS_API_KEY}&start=${userLocation.longitude},${userLocation.latitude}&end=${destination.longitude},${destination.latitude}`;
      console.log("üåç API-Anfrage:", url);

      const response = await fetch(url);
      if (!response.ok) throw new Error(`HTTP Fehler: ${response.status}`);

      const data = await response.json();
      console.log("üì• API-Antwort:", data);

      if (!data.routes || data.routes.length === 0) {
        console.warn("‚ùå Keine Route gefunden!");
        return;
      }

      // Koordinaten f√ºr die Route extrahieren
      const routeCoords = data.routes[0].geometry.coordinates.map(([lon, lat]) => [lat, lon]);
      console.log("üó∫Ô∏è Route erhalten:", routeCoords);

      // Route auf der Karte einzeichnen
      routeLayerRef.current = L.polyline(routeCoords, {
        color: "blue",
        weight: 5,
        opacity: 0.7,
      }).addTo(map);

      console.log("‚úÖ Route erfolgreich gezeichnet!");
    } catch (error) {
      console.error("‚ö†Ô∏è Fehler bei der Routenberechnung:", error);
    }
  };

  return (
    <div className="App">
      <div className="status-bar">
        <div>
          Letzte Aktualisierung: {lastUpdated?.toLocaleTimeString() || "Wird geladen..."}
        </div>
        <button onClick={fetchData} className="refresh-button">
          üîÑ Neu laden
        </button>
      </div>

      <MapContainer
        center={userLocation ? [userLocation.latitude, userLocation.longitude] : [52.52, 13.405]}
        zoom={13}
        style={{ height: "80vh", width: "100%" }}
        whenCreated={(map) => (mapRef.current = map)}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        />

        {userLocation && (
          <Marker
            position={[userLocation.latitude, userLocation.longitude]}
            icon={L.icon({
              iconUrl: "/icons/location.svg",
              iconSize: [30, 30],
              iconAnchor: [15, 30],
            })}
          >
            <Popup>Dein Standort</Popup>
          </Marker>
        )}

        {bins.map((bin) => (
          <Marker
            key={bin.id}
            position={[bin.latitude, bin.longitude]}
            icon={getBinIcon(bin.fill_level)}
            eventHandlers={{
              click: () => {
                if (!userLocation) {
                  console.warn("‚ö†Ô∏è Benutzerstandort nicht verf√ºgbar!");
                  return;
                }
                calculateRoute(bin);
              },
            }}
          >
            <Popup>
              <div className="popup">
                <h4>{bin.name}</h4>
                <div
                  className="fill-level"
                  style={{
                    width: `${bin.fill_level}%`,
                    backgroundColor:
                      bin.fill_level > 66
                        ? "#f44336"
                        : bin.fill_level > 33
                        ? "#ffc107"
                        : "#4caf50",
                  }}
                />
                <p>{bin.fill_level}% gef√ºllt</p>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default MapComponent;
